import "./create-recipe.scss";
import React, { useState } from "react";
import axios from "axios";
import { useGetUserID } from "../hooks/useGetUserID";
import { useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";

export const CreateRecipe = () => {
  const userID = useGetUserID();
  const [cookies, _] = useCookies(["access_token"]);
  const [recipe, setRecipe] = useState({
    name: "",
    description: "",
    ingredients: [], // array
    instructions: "",
    imageUrl: "",
    cookingTime: 0,
    userOwner: userID,
  });
  const [newIngredientIndex, setNewIngredientIndex] = useState(null); // New state to track the index of the newly added ingredient

  const navigate = useNavigate();

  const handleChange = (event) => {
    const { name, value } = event.target;
    setRecipe({ ...recipe, [name]: value });
  };

  const handleIngredientChange = (event, index) => {
    const { value } = event.target;
    const ingredients = [...recipe.ingredients]; // a replica of current ingredients list
    ingredients[index] = value;
    setRecipe({ ...recipe, ingredients });
  };

  const handleAddIngredient = () => {
    const ingredients = [...recipe.ingredients, ""];
    setRecipe({ ...recipe, ingredients });
    // setting the recipe object to be the same as before, whatever came after the comma will have changes, i.e. change to the ingredients field
    setNewIngredientIndex(ingredients.length - 1); // Set the index of the new ingredient to apply animation
    setTimeout(() => setNewIngredientIndex(null), 500); // Clear the animation class after 500ms
  };

  const isFormValid = () => {
    const hasFilledIngredients =
      recipe.ingredients.length > 0 &&
      recipe.ingredients.every((ingredient) => ingredient.trim() !== "");

    return (
      recipe.name.trim() !== "" &&
      recipe.description.trim() !== "" &&
      hasFilledIngredients &&
      recipe.instructions.trim() !== "" &&
      recipe.imageUrl.trim() !== "" &&
      recipe.cookingTime > 0
    );
  };

  const handleSubmit = async (event) => {
    event.preventDefault(); // prevent page refreshing every time I hit submit button

    try {
      await axios.post(
        "https://server-recipe-app-copy.onrender.com/recipes",
        { ...recipe },
        {
          headers: { authorization: cookies.access_token },
        }
      );

      alert("Recipe Created");
      navigate("/"); // redirect to home page
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="create-recipe slide-up-fade-in">
      <h2>Create a Recipe</h2>
      <form onSubmit={handleSubmit}>
        <label htmlFor="name">Name</label>
        <input
          type="text"
          id="name"
          name="name"
          value={recipe.name}
          onChange={handleChange}
        />
        <label htmlFor="description">Description</label>
        <textarea
          id="description"
          name="description"
          value={recipe.description}
          onChange={handleChange}
        ></textarea>
        <label htmlFor="ingredients">Ingredients</label>
        {recipe.ingredients.map((ingredient, index) => (
          <input
            key={index}
            type="text"
            id="ingredients"
            name="ingredients"
            value={ingredient}
            onChange={(event) => handleIngredientChange(event, index)}
            className={newIngredientIndex === index ? "pop-jiggle" : ""} // Apply animation class if the index matches
          />
        ))}
        <button
          type="button"
          className="cr1-button fill"
          onClick={handleAddIngredient}
        >
          Add Ingredient
        </button>
        <label htmlFor="instructions">Instructions</label>
        <textarea
          id="instructions"
          name="instructions"
          value={recipe.instructions}
          onChange={handleChange}
        ></textarea>
        <label htmlFor="imageUrl">Image URL</label>
        <input
          type="text"
          id="imageUrl"
          name="imageUrl"
          value={recipe.imageUrl}
          onChange={handleChange}
        />
        <label htmlFor="cookingTime">Cooking Time (minutes)</label>
        <input
          type="number"
          id="cookingTime"
          name="cookingTime"
          value={recipe.cookingTime}
          onChange={handleChange}
        />
        <div className="nav__link">
          <button
            type="submit"
            className={`cr2-button ${
              isFormValid() ? "fill" : "disabled"
            } nav__link--text`}
            data-splitting
            disabled={!isFormValid()}
          >
            Abracadabra!
          </button>
        </div>
      </form>
    </div>
  );
};
